export default function reducer(state, action) {
  switch (action.type) {
    case "INITIAL_STATE":
      return {
        ...state,
        tasks: action.payload,
      };
    case "ADD_TODO":
      const newTodo = {
        task_name: action.payload,
        task_status: false,
      };
      return {
        ...state,
        tasks: [...state.tasks, newTodo],
      };
    case "DONE_TODO":
      const temp = [...state.tasks].map((item) => {
        if (item.id === action.payload.id) {
          return { ...action.payload, task_status: action.payload.status };
        } else return item;
      });
      return {
        ...state,
        tasks: [...temp],
      };
  }
}
