import { post, put } from "../utils/utils";
export default function reducer(state, action) {
  switch (action.type) {
    case "INITIAL_STATE":
      return {
        ...state,
        tasks: action.payload,
      };
    case "ADD_TODO":
      const newTodo = {
        task_name: action.payload,
        task_status: false,
      };
      post("/tasks", newTodo).then((resp) => {
        console.log(resp);
      });
      return {
        ...state,
        tasks: [...state.tasks, newTodo],
      };
    case "DONE_TODO":
      const temp = [...state.tasks].map((item) => {
        if (item.id === action.payload.id) {
          return {
            ...action.payload,
            task_status: !action.payload.task_status,
          };
        } else return item;
      });
      const edited = temp.filter((task) => task.id === action.payload.id);
      put(`/task/${action.payload.id}`, { ...edited[0] }).then((resp) =>
        console.log(resp)
      );
      return {
        ...state,
        tasks: [...temp],
      };
  }
}
