import { useState, useContext, useEffect } from "react";
import Layout from "../components/Layout";
import { get, post } from "../utils/utils";
import { StateContext } from "../context/Context";

const Index = (props) => {
  const { state, dispatch } = useContext(StateContext);
  const [task, setTask] = useState("");
  useEffect(() => {
    dispatch({ type: "INITIAL_STATE", payload: props.tasks });
  }, []);
  const submit = (e) => {
    e.preventDefault();
    post("/tasks", { task_name: task, task_status: false }).then((res) => {
      dispatch({ type: "ADD_TODO", payload: res.data });
      setTask("");
    });
  };
  console.log(state);
  return (
    <Layout>
      <div className="alert alert-dismissible alert-warning">
        <button type="button" className="close">
          &times;
        </button>
        <h4 className="alert-heading">Warning!</h4>
        <p className="mb-0">
          Best check yo self, you're not looking too good. Nulla vitae elit
          libero, a pharetra augue. Praesent commodo cursus magna,{" "}
          <a href="#" className="alert-link">
            vel scelerisque nisl consectetur et
          </a>
          .
        </p>
      </div>
      <div>
        <form onSubmit={submit}>
          <input onChange={(e) => setTask(e.target.value)} value={task} />
          <button type="submit">Add Task</button>
        </form>
      </div>
      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr" }}>
        {state.tasks.length > 0 &&
          state.tasks.map((task) => (
            <>
              <input
                type="checkbox"
                checked={task.task_status}
                onChange={() => dispatch({ type: "DONE_TODO", payload: task })}
              />
              <span
                style={
                  task.task_status
                    ? { textDecoration: "line-through" }
                    : { textDecoration: "none" }
                }
              >
                {task.task_name}
              </span>
            </>
          ))}
      </div>
    </Layout>
  );
};

Index.getInitialProps = async () => {
  const response = await get("/tasks");
  const tasks = await response.data;
  return { tasks };
};

export default Index;
