import { useState, useContext, useEffect } from "react";
import Layout from "../components/Layout";
import { get, post } from "../utils/utils";
import { StateContext } from "../context/Context";

const Index = (props) => {
  const { state, dispatch } = useContext(StateContext);
  const [task, setTask] = useState("");
  useEffect(() => {
    dispatch({ type: "INITIAL_STATE", payload: props.tasks });
  }, []);
  const submit = (e) => {
    e.preventDefault();
    post("/tasks", { task_name: task, task_status: false }).then((res) => {
      dispatch({ type: "ADD_TODO", payload: res.data });
      setTask("");
    });
  };
  const rowClass = (task) => {
    return task.task_status ? "table-primary" : "table-success";
  };

  return (
    <Layout>
      <style jsx>
        {`
          .input-container {
            display: grid;
            grid-template-columns: 1fr 100px;
            width: 485px;
            margin-right: 1%;
            grid-gap: 15px;
          }
        `}
      </style>
      <div>
        <form className="input-container" onSubmit={submit}>
          <input
            width="485px"
            onChange={(e) => setTask(e.target.value)}
            value={task}
            className="form-control"
            placeholder="Enter a task for today"
          />
          <button className="btn btn-primary" type="submit">
            Add Task
          </button>
        </form>
      </div>
      <table className="table table-hover">
        <thead>
          <tr>
            <th scope="col">Type</th>
            <th scope="col">Description</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {state.tasks.length > 0 &&
            state.tasks.map((task) => (
              <tr className={rowClass(task)}>
                <th scope="row">
                  <input
                    type="checkbox"
                    checked={task.task_status}
                    onChange={() =>
                      dispatch({ type: "DONE_TODO", payload: task })
                    }
                  />
                </th>
                <td>{task.task_name}</td>
                <td>Edit | Delete</td>
              </tr>
            ))}
        </tbody>
      </table>
      {/* <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr" }}>
        {state.tasks.length > 0 &&
          state.tasks.map((task) => (
            <>
              <input
                type="checkbox"
                checked={task.task_status}
                onChange={() => dispatch({ type: "DONE_TODO", payload: task })}
              />
              <span
                style={
                  task.task_status
                    ? { textDecoration: "line-through" }
                    : { textDecoration: "none" }
                }
              >
                {task.task_name}
              </span>
            </>
          ))}
      </div> */}
    </Layout>
  );
};

Index.getInitialProps = async () => {
  const response = await get("/tasks");
  const tasks = await response.data;
  return { tasks };
};

export default Index;
