import Layout from "../components/Layout";
import { get } from "../utils/utils";
const Index = (props) => {
  return (
    <Layout>
      <div>
        <h1>To Do App</h1>
      </div>
    </Layout>
  );
};

Index.getInitialProps = async () => {
  const response = await get("http://localhost:3333/api/tasks");
  const tasks = await response.json();
  return { tasks };
};

export default Index;
